package systemcontractupgrade

import (
	"encoding/hex"
	"fmt"
	"math/big"

	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/core/state"
)

var (
	TokenHubUpgradeHeight      = big.NewInt(53000)
	TokenHubContract           = common.HexToAddress("0x0000000000000000000000000000000000001004")


	TokenHubUpgradeHeight1      = big.NewInt(54400)

	BSCValidatorContract           = common.HexToAddress("0x0000000000000000000000000000000000001000")
	BSCValidatorContractUpgradeHeight = big.NewInt(54650)
)

func UpgradeBuildInSystemContract(blockNumber *big.Int, statedb *state.StateDB) {
	// Exactly on the upgrade height
	if TokenHubUpgradeHeight.Cmp(blockNumber) == 0 {
		fmt.Println("Upgrade tokenhub code at "+ blockNumber.String())
		// code from https://github.com/binance-chain/bsc-genesis-contract/blob/batch-transfer-all/contracts/TokenHub.sol
		newContractCode, err := hex.DecodeString("")
		if err != nil {
			return
		}
		statedb.SetCode(TokenHubContract, newContractCode)
	}
	if TokenHubUpgradeHeight1.Cmp(blockNumber) == 0 {
		fmt.Println("Upgrade tokenhub code at "+ blockNumber.String())
		// code from https://github.com/binance-chain/bsc-genesis-contract/blob/batch-transfer-all/contracts/TokenHub.sol
		newContractCode, err := hex.DecodeString("")
		if err != nil {
			return
		}
		statedb.SetCode(TokenHubContract, newContractCode)
	}
	if BSCValidatorContractUpgradeHeight.Cmp(blockNumber) == 0 {
		fmt.Println("Upgrade tokenhub code at "+ blockNumber.String())
		// code from https://github.com/binance-chain/bsc-genesis-contract/blob/batch-transfer-all/contracts/TokenHub.sol
		newContractCode, err := hex.DecodeString("")
		if err != nil {
			return
		}
		statedb.SetCode(BSCValidatorContract, newContractCode)
	}
	/*
	  upgrade other build-in system contracts
	*/
}
